part of _controllers;

/**
* "data:image/png;base64,"
*/

pasteImage(Request req) {
    _getPostData(req, (postData) {
        var body = postData['file'];
        var prefix = "data:image/png;base64,";
        var bStr = body.substring(prefix.length);
        print("### bStr: $bStr");
        var bs = CryptoUtils.base64StringToBytes(bStr);
        print("### bs: ${bs.runtimeType}");
        var imgPath = "uploads/images/${nextFileName()}.png";
        var file = new File("web/$imgPath");
        file.writeAsBytesSync(bs);
        req.response.send("/$imgPath");
    });
}

const fileType = ';base64,';

uploadFile(Request req) {
    _getPostData(req, (postData) {
        var filename = postData['filename'];
        var fileData = postData['data'];

        print("###############################################################");
        print(fileData);


        var base64 = fileData.substring(fileData.indexOf(fileType) + fileType.length);
        var bin = CryptoUtils.base64StringToBytes(base64);
        var suffix = filename.split(new RegExp("[.]")).last.toLowerCase();
        if (suffix == "png" || suffix == "gif" || suffix == "jpg" || suffix == "jpeg") {
            var imgPath = "uploads/images/${nextFileName()}.$suffix";
            var file = new File("web/$imgPath");
            file.writeAsBytes(bin).then((_) {
                req.response.send(json.stringify({
                    'isImage': true,
                    'url': imgPath
                }));
            });
        } else {
            var filePath = "uploads/files/${nextFileName()}.$suffix";
            var file = new File("web/$filePath");
            file.writeAsBytes(bin).then((_) {
                req.response.send(json.stringify({
                    'isImage': false,
                    'url': filePath
                }));
            });
        }
    });
}
